package com.ljt.study.tools.http;

import okhttp3.*;

import java.io.IOException;

/**
 * @author LiJingTang
 * @date 2021-09-24 17:45
 */
class OkHttpTest {

    static final String CHECK_VALUE = "";
    static final String URL = "https://hfpay.cloudpnr.com/api/hfpay/alse/qry004";

    public static void main(String[] args) throws IOException {
        long start = System.currentTimeMillis();

        for (int i = 0; i < 50; i++) {
            RequestBody reqBody = new FormBody.Builder()
                    .add("mer_cust_id", "6666000107422135")
                    .add("version", "10")
                    .add("check_value", CHECK_VALUE)
                    .build();
            Request request = new Request.Builder()
                    .url(URL)
                    .post(reqBody)
                    .build();

            OkHttpClient httpClient = new OkHttpClient();
            Call call = httpClient.newCall(request);
            Response response = call.execute();

            System.out.println(i + "-" + response.code());
        }
        System.out.println(System.currentTimeMillis() - start);
    }

}
